--end of local variables 


local hits = 0
local misses = 0
local painthitrate = ui_new_checkbox("LUA", "A", "Paint Hitrate")
local painthitrate_s = ui_new_slider("LUA", "A", "Paint Hitrate Offset", 0, 1000, 196)
local function on_player_hurt(e)
    local attacker_id = client_userid_to_entindex(e.attacker)
    if attacker_id == nil then
        return
    end

    if attacker_id ~= entity_get_local_player() then
        return
    end

	hits = hits + 1
	
end


local function on_aim_miss(e)
	misses = misses + 1
end

local function on_paint(c)
	if (ui_get(painthitrate)) then
	local scrsize_x, scrsize_y = client_screen_size()
	local center_y = scrsize_y / 2
	local num = ui_get(painthitrate_s) * 10
	local center_x = scrsize_x - num
	local total = misses + hits
	local hitPercent = hits / total
	local fixed = 0
	if (hitPercent > 0)  then
	fixed = hitPercent * 100
	fixed = string.format("%2.1f", fixed)
	end

    client_draw_text(c, center_x + 45, center_y, 249, 112, 213, 255, "+", 0, "hits: ", hits)
	client_draw_text(c, center_x + 45, center_y + 20, 249, 112, 213, 255, "+", 0, "misses: ", misses)
	client_draw_text(c, center_x + 45, center_y + 40, 249, 112, 213, 255, "+", 0, "total: ", total)
	client_draw_text(c, center_x + 45, center_y + 60, 249, 112, 213, 255, "+", 0, "percent: ", fixed, "%")
	end

end


client_set_event_callback('player_hurt', on_player_hurt)
client_set_event_callback('aim_miss', on_aim_miss)
--on connect reset hits and misses
client_set_event_callback("player_connect_full", function(e)
	if client.userid_to_entindex(e.userid) ~= entity.get_local_player() then
		return
	end
    misses = 0
	hits = 0
end)
client_set_event_callback("paint", on_paint)